DROP TABLE IF EXISTS `STUDENT_EXERCISE`;

CREATE TABLE STUDENT_EXERCISE(
	EXID INT ,
	SID INT,
	NUMBER_OF_ATTEMPTS INT DEFAULT 0 NOT NULL,
	NUMBER_OF_HINTS INT DEFAULT 0 NOT NULL,
	NUMBER_OF_INCORRECT_ATTEMPTS INT DEFAULT 0 NOT NULL,
	SOLVED BOOL DEFAULT FALSE,
	ABILITY DOUBLE(20,10) DEFAULT 0 NOT NULL,
	DISCRIMINATION DOUBLE(20,10) DEFAULT 0 NOT NULL,
	DIFFICULTY DOUBLE(20,10) DEFAULT 0 NOT NULL,
	TIMESPENT DOUBLE(20,10) DEFAULT 0 NOT NULL,
	SECID INT ,
	CHID INT ,
	BOOKID INT,
	FOREIGN KEY (SECID) REFERENCES SECTION(SECID),
	FOREIGN KEY (CHID) REFERENCES CHAPTER(CHID),
	FOREIGN KEY (BOOKID) REFERENCES BOOK(BOOKID)
);


UPDATE STUDENT_EXERCISE AS SE 
JOIN EXERCISE AS E
ON SE.EXID = E.EXID
SET SE.SECID = E.SECID
WHERE SE.EXID = E.EXID;


UPDATE STUDENT_EXERCISE AS SE 
JOIN SECTION AS SEC
ON SE.SECID = SEC.SECID
SET SE.CHID = SEC.CHID
WHERE SE.SECID = SEC.SECID;

UPDATE STUDENT_EXERCISE AS SE 
JOIN CHAPTER AS CH
ON SE.CHID = CH.CHID
SET SE.BOOKID = CH.BOOKID
WHERE SE.CHID = CH.CHID;